mapper.select:
SELECT "composite_key"						AS "cmpyKey"
	 , "news_date"							AS "newsDate"
	 , "company_name"						AS "cmpyName"
	 , round(cast(ta_score as numeric),4)	AS "taScre"
	 , "news_title"							AS "newsTtl"
	 , "ext_morphs"							AS "extMorp"

 FROM NLP."nlp_news"
 where (company_name like concat('%',#{searchWord},'%') or raw_data like concat('%',#{searchWord},'%') or news_title like concat('%',#{searchWord},'%') )


  		and year = #{newsYear}


  		and year = #{newsYear}
  		and month = #{gubunItem}


  		and year = #{newsYear}
  		and quarter = #{gubunItem}


  		and news_date between  to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')



 order by news_date desc

SELECT "company" as "company"
	 , "date" as "date"
	 , "closing_price" as "price"	

 from (select * , date_part('year', date) as year, date_part('month', date) as month, date_part('day', date) as day, date_part('quarter', date) as quarter
		from (select seq, company_code, company, to_date(date, 'yyyymmdd') as date, closing_price from nlp.nlp_stock) as a) as b
 WHERE company LIKE concat('%',#{searchWord},'%')


  		and year = #{newsYear}


  		and year = #{newsYear}
  		and month = #{gubunItem}


  		and year = #{newsYear}
  		and quarter = #{gubunItem}


  		and date between  to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')


 ORDER BY date

SELECT "composite_key"						AS "cmpyKey"
	 , "news_date"							AS "newsDate"
	 , "company_name"						AS "cmpyName"
	 , round(cast(ta_score as numeric),4)	AS "taScre"
	 , "news_title"							AS "newsTtl"
	 , "ext_morphs"							AS "extMorp"
 FROM NLP."nlp_news"
 where industry_code = (select industry_code from nlp.company_code where industry_name like concat('%',#{selectedName},'%') limit 1)


  		and year = #{newsYear}


  		and year = #{newsYear}
  		and month = #{gubunItem}


  		and year = #{newsYear}
  		and quarter = #{gubunItem}


  		and news_date between  to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')



 order by news_date desc

select d.date, d.company, d.closing_price as "price" from ( select s.company, to_date(s.date, 'yyyymmdd') as date, date_part('year', to_date(s.date, 'yyyymmdd')) as year, date_part('month', to_date(s.date, 'yyyymmdd')) as month, date_part('day', to_date(s.date, 'yyyymmdd')) as day, date_part('quarter', to_date(s.date, 'yyyymmdd')) as quarter, s.closing_price from nlp.nlp_stock as s join nlp.company_code as b on (s.company_code = b.code and b.industry_name = #{selectedName}))as d
where
year = #{newsYear}
year = #{newsYear} and month = #{gubunItem}
year = #{newsYear} and quarter = #{gubunItem}
date between to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')
ORDER BY date

SELECT "composite_key"						AS "cmpyKey"
	 , "news_date"							AS "newsDate"
	 , "company_name"						AS "cmpyName"
	 , round(cast(ta_score as numeric),4)	AS "taScre"
	 , "news_title"							AS "newsTtl"
	 , "ext_morphs"							AS "extMorp"
 FROM NLP."nlp_news"
 where system_code = (select system_code from nlp.company_code where system_name like concat('%',#{selectedName},'%') limit 1)


  		and year = #{newsYear}


  		and year = #{newsYear}
  		and month = #{gubunItem}


  		and year = #{newsYear}
  		and quarter = #{gubunItem}


  		and news_date between  to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')



 order by news_date desc

select d.date, d.company, d.closing_price as "price" from ( select s.company, to_date(s.date, 'yyyymmdd') as date, date_part('year', to_date(s.date, 'yyyymmdd')) as year, date_part('month', to_date(s.date, 'yyyymmdd')) as month, date_part('day', to_date(s.date, 'yyyymmdd')) as day, date_part('quarter', to_date(s.date, 'yyyymmdd')) as quarter, s.closing_price from nlp.nlp_stock as s join nlp.company_code as b on (s.company_code = b.code and b.system_name = #{selectedName}))as d
where
year = #{newsYear}
year = #{newsYear} and month = #{gubunItem}
year = #{newsYear} and quarter = #{gubunItem}
date between to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')
ORDER BY date

SELECT "composite_key"						AS "cmpyKey"
	 , "news_date"							AS "newsDate"
	 , "company_name"						AS "cmpyName"
	 , round(cast(ta_score as numeric),4)	AS "taScre"
	 , "news_title"							AS "newsTtl"
	 , "ext_morphs"							AS "extMorp"
 FROM NLP."nlp_news"
 where company_name = #{selectedName}


  		and year = #{newsYear}


  		and year = #{newsYear}
  		and month = #{gubunItem}


  		and year = #{newsYear}
  		and quarter = #{gubunItem}


  		and news_date between  to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')



 order by news_date desc

SELECT "company" as "company"
	 , "date" as "date"
	 , "closing_price" as "price"	

 from (select * , date_part('year', date) as year, date_part('month', date) as month, date_part('day', date) as day, date_part('quarter', date) as quarter
		from (select seq, company_code, company, to_date(date, 'yyyymmdd') as date, closing_price from nlp.nlp_stock) as a) as b
 WHERE company = #{selectedName}


  		and year = #{newsYear}


  		and year = #{newsYear}
  		and month = #{gubunItem}


  		and year = #{newsYear}
  		and quarter = #{gubunItem}


  		and date between  to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')


 ORDER BY date

SELECT "composite_key"						AS "cmpyKey"
	 , "news_date"							AS "newsDate"
	 , "company_name"						AS "cmpyName"
	 , round(cast(ta_score as numeric),4)	AS "taScre"
	 , "news_title"							AS "newsTtl"
	 , "ext_morphs"							AS "extMorp"
  FROM NLP."nlp_news"
  where subsidiary_code = (select subsidiary_code from nlp.company_code where subsidiary_name = #{selectedName} limit 1)


  		and year = #{newsYear}


  		and year = #{newsYear}
  		and month = #{gubunItem}


  		and year = #{newsYear}
  		and quarter = #{gubunItem}


  		and news_date between  to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')


  order by news_date desc

select d.date, d.company, d.closing_price as "price" from ( select s.company, to_date(s.date, 'yyyymmdd') as date, date_part('year', to_date(s.date, 'yyyymmdd')) as year, date_part('month', to_date(s.date, 'yyyymmdd')) as month, date_part('day', to_date(s.date, 'yyyymmdd')) as day, date_part('quarter', to_date(s.date, 'yyyymmdd')) as quarter, s.closing_price from nlp.nlp_stock as s join nlp.company_code as b on (s.company_code = b.code and b.subsidiary_name = #{selectedName}))as d
where
year = #{newsYear}
year = #{newsYear} and month = #{gubunItem}
year = #{newsYear} and quarter = #{gubunItem}
date between to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')
ORDER BY date

select distinct on (d.date) d.name, d.date, d.value from ( select s.name, s.code, s.sinyong_date as date, date_part('year', s.sinyong_date) as year, date_part('month', s.sinyong_date) as month, date_part('day', s.sinyong_date) as day, date_part('quarter', s.sinyong_date) as quarter, s.amount as value
from nlp.sinyonggy_subsidiary as s join nlp.company_code as b on (s.code = b.subsidiary_code and b.subsidiary_name = #{selectedName})) as d where
from nlp.sinyonggy_industry as s join nlp.company_code as b on (s.code = b.system_code and b.system_name = #{selectedName})) as d where
from nlp.sinyonggy_industry as s join nlp.company_code as b on (s.code = b.system_code and b.industry_name = #{selectedName})) as d where
from nlp.sinyonggy_company as s ) as d where d.name = #{selectedName} and
year = #{newsYear}
year = #{newsYear} and month = #{gubunItem}
year = #{newsYear} and quarter = #{gubunItem}
date between to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')
ORDER BY date

select distinct on (d.date) d.name, d.date, d.value from ( select s.code,
s.name, s.salesamt_date as date, date_part('year', s.salesamt_date) as year, date_part('month', s.salesamt_date) as month, date_part('day', s.salesamt_date) as day, date_part('quarter', s.salesamt_date) as quarter, s.amount as value from nlp.salesamt_company as s ) as d where d.name = #{selectedName} and
s.name, to_date(s.salesamt_year::text, 'YYYY') as date, date_part('year', to_date(s.salesamt_year::text, 'YYYY')) as year, date_part('month', to_date(s.salesamt_year::text, 'YYYY')) as month, date_part('day', to_date(s.salesamt_year::text, 'YYYY')) as day, date_part('quarter', to_date(s.salesamt_year::text, 'YYYY')) as quarter, s.amount as value from nlp.salesamt_subsidiary as s join nlp.company_code as b on (s.code = b.subsidiary_code and b.subsidiary_name = #{selectedName}) ) as d where
s.code as name, to_date(s.salesamt_year::text, 'YYYY') as date, date_part('year', to_date(s.salesamt_year::text, 'YYYY')) as year, date_part('month', to_date(s.salesamt_year::text, 'YYYY')) as month, date_part('day', to_date(s.salesamt_year::text, 'YYYY')) as day, date_part('quarter', to_date(s.salesamt_year::text, 'YYYY')) as quarter, s.amount as value from nlp.salesamt_industry as s join nlp.company_code as b
on (s.code = b.system_code and b.system_name = #{selectedName})
on (s.code = b.system_code and b.industry_name = #{selectedName})
) as d where
year = #{newsYear}
year = #{newsYear} and month = #{gubunItem}
year = #{newsYear} and quarter = #{gubunItem}
date between to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')
ORDER BY date

select distinct on (d.date) d.name, d.date, d.value from ( select s.code,
s.name, s.grade_date as date, date_part('year', s.grade_date) as year, date_part('month', s.grade_date) as month, date_part('day', s.grade_date) as day, date_part('quarter', s.grade_date) as quarter, s.danger_name as value from nlp.dangergrade_company as s ) as d where d.name = #{selectedName} and
s.name, to_date(s.grade_year::text, 'YYYY') as date, date_part('year', to_date(s.grade_year::text, 'YYYY')) as year, date_part('month', to_date(s.grade_year::text, 'YYYY')) as month, date_part('day', to_date(s.grade_year::text, 'YYYY')) as day, date_part('quarter', to_date(s.grade_year::text, 'YYYY')) as quarter, s.danger_name as value from nlp.dangergrade_subsidiary as s join nlp.company_code as b on (s.code = b.subsidiary_code and b.subsidiary_name = #{selectedName}) ) as d where
s.code as name, format('%s-%s-01', s.grade_year, 1 + (s.grade_quarter - 1) * 3)::date as date, date_part('year', format('%s-%s-01', s.grade_year, 1 + (s.grade_quarter - 1) * 3)::date) as year, date_part('month', format('%s-%s-01', s.grade_year, 1 + (s.grade_quarter - 1) * 3)::date) as month, date_part('day', format('%s-%s-01', s.grade_year, 1 + (s.grade_quarter - 1) * 3)::date) as day, date_part('quarter', format('%s-%s-01', s.grade_year, 1 + (s.grade_quarter - 1) * 3)::date) as quarter, s.danger_name as value from nlp.dangergrade_industry as s join nlp.company_code as b
on (s.code = b.system_code and b.system_name = #{selectedName})
on (s.code = b.system_code and b.industry_name = #{selectedName})
) as d where
year = #{newsYear}
year = #{newsYear} and month = #{gubunItem}
year = #{newsYear} and quarter = #{gubunItem}
date between to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')
ORDER BY date

select distinct on (d.date)
	d.name, d.date, d.value from (
		select s.code,
			s.name, 
			s.creditgrade_date as date, 
			date_part('year', s.creditgrade_date) as year, 
		 	date_part('month', s.creditgrade_date) as month,
		 	date_part('day', s.creditgrade_date) as day, 
		 	date_part('quarter', s.creditgrade_date) as quarter, 
			s.grade as value
		from nlp.creditgrade_company as s
	) as d
	where d.name = #{selectedName} and



  		year = #{newsYear}


  		year = #{newsYear}
  		and month = #{gubunItem}


  		year = #{newsYear}
  		and quarter = #{gubunItem}


  		date between  to_date(#{startDate}, 'yyyy-mm-dd') and to_date(#{endDate}, 'yyyy-mm-dd')


ORDER BY date
